import React, { useEffect, useState } from 'react';
import { Button, ButtonGroup, Container, Table } from 'reactstrap';
import { Link } from 'react-router-dom';
import Header from './components/Header';

const PublicationList = () => {

  const [publications, setPublications] = useState([]);
  const [loading, setLoading] = useState(false);
  const urlBASE = "http://localhost:8080/";

  useEffect(() => {
    setLoading(true);

    fetch(urlBASE+"findAll")
      .then(response => response.json())
      .then(data => {
        setPublications(data);
        setLoading(false);
      })
  }, []);

  const remove = async (id) => {
    await fetch(`${urlBASE}/findByID/${id}`, {
      method: 'DELETE',
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json'
      }
    }).then(() => {
      let updatedPublications = [...publications].filter(i => i.id !== id);
      setPublications(updatedPublications);
    });
  }

  if (loading) {
    return <p>Loading...</p>;
  }

  const publicationList = publications.map(publication => {
    const title = `${publication.title || ''}`;
    const body = `${publication.body || ''}`;
    return <tr key={publication.id}>
      <td style={{whiteSpace: 'nowrap'}}></td>
      <td>{title}</td>
      <td>{body}</td>
      <td>
        <ButtonGroup>
          <Button size="sm" color="primary" tag={Link} to={"/publications/" + publications.id}>Edit</Button>
          <Button size="sm" color="danger" onClick={() => remove(publications.id)}>Delete</Button>
        </ButtonGroup>
      </td>
    </tr>
  });

  return (
    <div>
        <Header />
      <Container fluid>
        <div className="float-end">
          <Button color="success" tag={Link} to="/publications/new">Nueva Publicación</Button>
        </div>
        <h3>Publicaciones</h3>
        <Table className="mt-4">
          <thead>
          <tr>
            <th width="20%">Título</th>
            <th width="20%">Entrada</th>
            <th width="10%">Acciones</th>
          </tr>
          </thead>
          <tbody>
          {publicationList}
          </tbody>
        </Table>
      </Container>
    </div>
  );
};

export default PublicationList;
