export default class PublicationList extends Component {

    loadPublications = (event) => {
        event.preventDefault();

        const url = "http://localhost:8080/publications/findAll";
        const header = {
            method: "GET",
            headers: {
                "credentials": localStorage.credential,
                "Content-Type": "application/json"
            }
        }

        fetch(url, header)
            .then(response => {
                if (!response.ok) throw Error(response.status);
                return response.json();
            }
            )
            .then(json => {
                console.log(json);
                localStorage.uuid = json.uuid;
                localStorage.credential = json.credential;
                window.location.href = "/publications";
            })
            .catch(error => {
                console.error(error);
                localStorage.clear();
                alert("Credenciales Incorrectas");
            });
        this.cleanValues();
    }

