import React from "react";
import { Component } from "react";
import Header from './Header';
import '../resources/css/bootstrap.css';

export default class Register extends Component {
  constructor(props) {
    super(props);
    this.state = {
      email: "",
      key: "",
      active: false
    }
  }
  setValues = (event) => {
    this.setState({
      [event.target.id]: event.target.value
    });
  }

  cleanValues = () => {
    this.setState(
      {
        email: "",
        key: "",
        active: false
      }
    );
  }

  register = (event) => {
    event.preventDefault();

    const url = "http://localhost:8080//users/insert";
    const user = {
      email: this.state.email,
      key: this.state.key,
      active: this.state.active
    }
    const header = {
      method: "POST",
      body: JSON.stringify(user),
      headers: {
        "credentials": localStorage.credential,
        "Content-Type": "application/json"
      }
    }

    fetch(url, header)
      .then(response => {
        if (!response.ok) throw Error(response.status);
        return response.json();
      }
      )
      .then(json => {
        console.log(json);
        localStorage.uuid = json.uuid;
        localStorage.credential = json.credential;
        window.location.href = "/login";
      })
      .catch(error => {
        console.error(error);
        localStorage.clear();
        alert("Credenciales Incorrectas");
      });
    this.cleanValues();
  }

  render() {
    return (

      <><div><Header /></div>
 <div class="modal fade" id="signIn" data-bs-backdrop="static" data-bs-keyboard="false" tabindex="-1" aria-labelledby="staticBackdropLabel" aria-hidden="true"></div>
        <div class="modal-dialog modal-dialog-centered">
          <div class="modal-content">
            <div class="modal-header">
              <h4 class="modal-title fw-bold w-100 text-center" id="signIn">Registrarse</h4>
              <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
            </div>
            <div class="modal-body">
              <form onSubmit={this.register}>
                <fieldset>
                  <div className="form-group mb-2">
                    <label for="exampleInputEmail1" className="form-label mt-4">Correo Electrónico</label>
                    <input
                      type="email"
                      className="form-control"
                      id="email"
                      name="email"
                      aria-describedby="emailHelp"
                      placeholder="Ingrese su correo electrónico"
                      value={this.state.email}
                      onChange={this.setValues}
                    />
                    <small id="emailHelp" className="form-text text-muted">No compartiremos su correo electrónico con nadie más.</small>
                  </div>
                  <div className="form-group mb-2">
                    <label for="key" className="form-label mt-4">Contraseña</label>
                    <input
                      type="password"
                      className="form-control"
                      id="key"
                      name="key"
                      placeholder="Contraseña"
                      required={true}
                      value={this.state.key}
                      onChange={this.setValues}
                    />
                  </div>
                  <div className="d-grid gap-2">
                    <button className="btn btn-outline-primary p-2" type="submit">Ingresar</button>
                  </div>
                </fieldset>
              </form>
            </div>
          </div>
          <div class="modal-footer">
            <button type="button" class="btn btn-secondary" data-bs-dismiss="modal">Close</button>
            <button type="button" class="btn btn-primary">Understood</button>
          </div>
        </div>

      </>
    );
  }
}
