import React from "react";
import { Component, useState } from "react";
import Header from './Header';
import '../resources/css/bootstrap.css';


export default class Publication extends Component {
    constructor(props) {
        super(props);
        this.state = {
            title: "",
            body: ""
        }
    }
    setValues = (event) => {
        this.setState({
            [event.target.id]: event.target.value
        });
    }

    cleanValues = () => {
        this.setState(
            {
                title: "",
                body: ""
            }
        );
    }

    postPublication = (event) => {
        event.preventDefault();

        const url = "http://localhost:8080/publications/insert";
        const publication = {
            title: this.state.title,
            body: this.state.body
        }
        const header = {
            method: "POST",
            body: JSON.stringify(publication),
            headers: {
                "key": localStorage.credential,
                "value": localStorage.uuid,
                "Content-Type": "application/json"
            }
        }

        fetch(url, header)
            .then(response => {
                if (!response.ok) throw Error(response.status);
                return response.json();
            }
            )
            .then(json => {
                console.log(json);
                localStorage.uuid = json.uuid;
                localStorage.credential = json.credential;
                window.location.href = "/publications/new-publication";
            })
            .catch(error => {
                console.error(error);
                localStorage.clear();
                alert("Credenciales Incorrectas");
            });
        this.cleanValues();
    }

    render() {
        return (

            <>
            <div></div>
                <div className="container bg-primary mt-5 shadow rounded " >
                    <div className="row align-items-stretch">
                        <div className="col bg d-none d-lg-block col-md-5 col-lg-5 col-xl-6 rounded-start"></div>
                        <div className="col bg-white rounded-end">
                            <div className="text-end">
                                <img src="https://static.educacionit.com/educacionit/assets/identidad/favicon/favicon.ico" alt="Logo" />
                                <h1 className="fw-bold text-center py-4">Nueva Publicación</h1>
                            </div>
                            <form onSubmit={this.postPublication}>
                                <fieldset>

                                    <div class="form-group">
                                        <label className="col-form-label mt-4" for="title">Título</label>
                                        <input type="text"
                                            className="form-control"
                                            placeholder="Título"
                                            id="title"
                                            name="title"
                                            value={this.state.title}
                                            onChange={this.setValues}
                                        />
                                    </div>

                                    <div className="form-group">
                                        <label for="body" class="form-label mt-4">Cuerpo de texto
                                            <textarea class="form-control" id="exampleTextarea" rows="3"></textarea>
                                        </label>
                                        <input type="submit"
                                            placeholder="Título"
                                            id="body"
                                            name="body"
                                            value={this.state.body}
                                            onChange={this.setValues}
                                        />
                                    </div>
                                </fieldset>
                                <button type="submit" className="btn btn-primary">Publicar</button>
                            </form>
                        </div>
                    </div>
                </div>
            </>

        );
    }
}
