import React, { useEffect, useState } from 'react';
import { Button, ButtonGroup, Container, Table } from 'reactstrap';
import { Link } from 'react-router-dom';
import Header from './Header';
import Publication from './Publication';
import '../resources/css/bootstrap.css';



const Publications = () => {

  const [publications, setPublications] = useState([]);
  const [loading, setLoading] = useState(false);
  const urlBASE = "http://localhost:8080/";

  useEffect(() => {
    setLoading(true);

    fetch(`http://localhost:8080/publications/findAll`)
      .then(response => response.json())
      .then(data => {
        setPublications(data);
        setLoading(false);
      })
  }, []);

  const remove = async (id) => {
    await fetch(`${urlBASE}/findByID/${id}`, {
      method: 'DELETE',
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json'
      }
    }).then(() => {
      let updatedPublications = [...publications].filter(i => i.id !== id);
      setPublications(updatedPublications);
    });
  }

  if (loading) {
    return <p>Loading...</p>;
  }

  const publicationList = publications.map(publication => {
    const title = `${publication.title || ''}`;
    const body = `${publication.body || ''}`;
    return <tr key={publication.id}>
      <td style={{ whiteSpace: 'nowrap' }}></td>
      <td>{title}</td>
      <td>{body}</td>
      <td>
        <ButtonGroup>
          <Button size="sm" color="primary" tag={Link} to={"/publications/" + publications.id}>Edit</Button>
          <Button size="sm" color="danger" onClick={() => remove(publications.id)}>Delete</Button>
        </ButtonGroup>
      </td>
    </tr>
  });

  return (
    <>
      <div><Header /></div>

      <div className='card-group'>
        {
          publications.map(publication => {
            return <Publication title={publication.title} body={publication.body}/>
          })
        }

      </div>
    </>
  )


}

export default Publications;
