import axios from "axios";

const PUBLICATIONS_API_BASE_URL = "http://localhost:8080/publications/findAll";

class PublicationService {
  savePublication(publication) {
    return axios.post(PUBLICATIONS_API_BASE_URL, publication);
  }

  getPublications() {
    return axios.get(PUBLICATIONS_API_BASE_URL);
  }

  getPublicationsById(id) {
    return axios.get(PUBLICATIONS_API_BASE_URL + "/" + id);
  }

}

export default new PublicationService();
