import React from "react";
import { Component } from "react";
import '../resources/css/bootstrap.css';
import '../resources/css/login-form.css';

export default class Login extends Component {
    constructor(props) {
        super(props);
        this.state = {
            email: "",
            key: ""
        }
    }
    setValues = (event) => {
        this.setState({
            [event.target.id]: event.target.value
        });
    }

    cleanValues = () => {
        this.setState(
            {
                email: "",
                key: ""
            }
        );
    }

    signIn = (event) => {
        event.preventDefault();

        const url = "http://localhost:8080/login/signIn";
        const user = {
            email: this.state.email,
            key: this.state.key
        }
        const header = {
            method: "POST",
            body: JSON.stringify(user),
            headers: {
                "Content-Type": "application/json"
            }
        }

        fetch(url, header)
            .then(response => {
                if (!response.ok) throw Error(response.status);
                return response.json();
            }
            )
            .then(json => {
                console.log(json);
                localStorage.uuid = json.uuid;
                localStorage.credential = json.credential;
                window.location.href = "/login";
            })
            .catch(error => {
                console.error(error);
                localStorage.clear();
                alert("Credenciales Incorrectas");
            });
        this.cleanValues();
    }



    render() {
        return (

            <>
                <div className="container w-75 bg-primary mt-5 shadow rounded">
                    <div className="row align-items-stretch">
                        <div className="col bg d-none d-lg-block col-md-5 col-lg-5 col-xl-6 rounded-start">

                        </div>
                        <div className="col bg-white rounded-end">
                            <div className="text-end">
                                <img src="https://static.educacionit.com/educacionit/assets/identidad/favicon/favicon.ico" alt="Logo" />
                                <h1 className="fw-bold text-center py-3">Bienvenido</h1>
                            </div>
                            <form onSubmit={this.signIn}>
                                <fieldset>
                                    <div className="form-group mb-2">
                                        <label for="exampleInputEmail1" className="form-label mt-4">Correo Electrónico</label>
                                        <input type="email" className="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" placeholder="Ingrese su correo electrónico"
                                            value={this.state.email}
                                            onChange={this.setValues}
                                        />
                                        <small id="emailHelp" className="form-text text-muted">No compartiremos su correo electrónico con nadie más.</small>
                                    </div>
                                    <div className="form-group mb-2">
                                        <label for="key" className="form-label mt-4">Contraseña</label>
                                        <input type="password" 
                                        className="form-control" 
                                        id="key" 
                                        placeholder="Contraseña"
                                            required={true}
                                            value={this.state.key}
                                            onChange={this.setValues}
                                        />

                                    </div>

                                    <div className="d-grid gap-2">
                                        <button className="btn btn-outline-primary p-2" type="submit">Ingresar</button>
                                    </div>

                                    <div className="form-check text-start m-2">
                                        <input className="form-check-input" type="checkbox" value="" id="flexCheckDefault" />
                                        <label className="form-check-label" for="flexCheckDefault">
                                            Mantenerme Conectado
                                        </label>
                                    </div>

                                    <div className="form-group">
                                        <span>¿Aún no tienes cuenta?
                                            <a href="#">Regístrate</a>
                                        </span>
                                    </div>

                                </fieldset>
                            </form>
                        </div>
                    </div>
                </div>

            </>

        );
    }
}
