import React from "react";
import { Component } from "react";
import '../resources/css/bootstrap.css';

export default class Login extends Component {
    constructor(props) {
        super(props);
        this.state = {
            email: "",
            key: ""
        }
    }
    setValues = (event) => {
        this.setState({
            [event.target.id]: event.target.value
        });
    }

    cleanValues = () => {
        this.setState(
            {
                email: "",
                key: ""
            }
        );
    }

    signIn = (event) => {
        event.preventDefault();

        const url = "http://localhost:8080/login/signIn";
        const user = {
            email: this.state.email,
            key: this.state.key
        }
        const header = {
            method: "POST",
            body: JSON.stringify(user),
            headers: {
                "Content-Type": "application/json"
            }
        }

        fetch(url, header)
            .then(response => {
                if (!response.ok) throw Error(response.status);
                return response.json();
            }
            )
            .then(json => {
                console.log(json);
                localStorage.uuid = json.uuid;
                localStorage.credential = json.credential;
                window.location.href = "/login";
            })
            .catch(error => {
                console.error(error);
                localStorage.clear();
                alert("Credenciales Incorrectas");
            });
        this.cleanValues();
    }



    render() {
        return (
            <>
                <div className="container">
                    <div className="row m">
                        <div className="col">

                        </div>
                        <div className="col">
                            <div className="text-end">
                                <img src="https://static.educacionit.com/educacionit/assets/imagotype-it-fill-v2-color.svg" alt="Logo" />
                                <h2 className="fw-bold text-center py-5">Bienvenido</h2>
                            </div>
                            <form>
                                <fieldset>
                                    <legend className="text-start py-4">Iniciar sesión</legend>

                                    <div class="form-group">
                                        <label for="exampleInputEmail1" class="form-label mt-4">Correo Electrónico</label>
                                        <input type="email" class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" placeholder="Ingrese su correo electrónico" />
                                        <small id="emailHelp" class="form-text text-muted">No compartiremos su correo electrónico con nadie más.</small>
                                    </div>
                                    <div class="form-group">
                                        <label for="exampleInputPassword1" class="form-label mt-4">Contraseña</label>
                                        <input type="password" class="form-control" id="exampleInputPassword1" placeholder="Password" />
                                    </div>

                                    <div class="d-grid gap-2 mb-">
                                        <button class="btn btn-outline-primary" type="button">Iniciar sesión</button>
                                    </div>

                                    <div className="form-check text-start">
                                        <input className="form-check-input" type="checkbox" value="" id="flexCheckDefault" />
                                        <label className="form-check-label" for="flexCheckDefault">
                                            Mantenerme Conectado
                                        </label>
                                    </div>

                                    <div class="form-group">
                                        <span>¿Aún no tienes cuenta?
                                            <a href="#">Regístrate</a>
                                        </span>
                                    </div>

                                </fieldset>
                            </form>
                        </div>
                    </div>
                </div>

            </>

        );
    }
}
